import React from 'react';
import { FaCode, FaMobile, FaServer, FaChartLine, FaShieldAlt, FaHeadset } from 'react-icons/fa';
import './TopServicesSection.css';

const TopServicesSection = () => {
  const services = [
    {
      icon: <FaCode />,
      title: 'Web Development',
      description: 'Custom web applications and websites built with modern technologies and best practices.',
      features: [
        'Responsive Design',
        'Progressive Web Apps',
        'E-commerce Solutions'
      ]
    },
    {
      icon: <FaMobile />,
      title: 'Mobile Development',
      description: 'Native and cross-platform mobile applications for iOS and Android devices.',
      features: [
        'iOS Development',
        'Android Development',
        'Cross-platform Solutions'
      ]
    },
    {
      icon: <FaServer />,
      title: 'Cloud Services',
      description: 'Scalable cloud infrastructure and deployment solutions for your applications.',
      features: [
        'Cloud Migration',
        'Server Management',
        'DevOps Solutions'
      ]
    },
    {
      icon: <FaChartLine />,
      title: 'Digital Marketing',
      description: 'Comprehensive digital marketing strategies to grow your online presence.',
      features: [
        'SEO Optimization',
        'Social Media Marketing',
        'Content Strategy'
      ]
    },
    {
      icon: <FaShieldAlt />,
      title: 'Cybersecurity',
      description: 'Advanced security solutions to protect your digital assets and data.',
      features: [
        'Security Audits',
        'Threat Detection',
        'Data Protection'
      ]
    },
    {
      icon: <FaHeadset />,
      title: 'IT Support',
      description: '24/7 technical support and maintenance services for your IT infrastructure.',
      features: [
        'Remote Support',
        'System Maintenance',
        'Technical Consulting'
      ]
    }
  ];

  return (
    <section className="services-section" id="services">
      <div className="services-container">
        <div className="services-header">
          <h2>Our Services</h2>
          <p>
            We offer a comprehensive range of services to help businesses thrive in the digital age.
            Our expert team is dedicated to delivering high-quality solutions tailored to your needs.
          </p>
        </div>

        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <div className="service-icon">
                {service.icon}
              </div>
              <h3 className="service-title">{service.title}</h3>
              <p className="service-description">{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="service-feature">
                    {feature}
                  </li>
                ))}
              </ul>
              <a href="#contact" className="service-link">
                Learn More
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TopServicesSection;
