import React, { useEffect, useState } from "react";
import { useLanguage } from "../../context/LanguageContext";
import "./FeaturesSection.css";

const FeaturesSection = () => {
  const { language, t } = useLanguage();
  const [features, setFeatures] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/features")
      .then(res => res.json())
      .then(data => setFeatures(data))
      .catch(() => setFeatures([]));
  }, []);

  const getTitle = (feature) => {
    if (language === "kz") return feature.TitleKZ || feature.title_kz;
    if (language === "en") return feature.TitleEN || feature.title_en;
    return feature.TitleRU || feature.title_ru;
  };

  const apiFeatures = (features || []).map(f => ({ text: getTitle(f) }));
  const staticFeatures = (t.featuresSection.features || []).map(text => ({ text }));
  const allFeatures = [
    ...apiFeatures,
    ...staticFeatures.filter(sf => !apiFeatures.some(af => af.text === sf.text))
  ];

  return (
    <section className="features-section">
      <div className="container">
        <h2 className="features-title">{t.featuresSection.title}</h2>
        <ul className="features-list">
          {allFeatures.map((feature, idx) => (
            <li className="feature-item" key={idx}>
              <span className="feature-icon">✔️</span>
              <span className="feature-text">{feature.text}</span>
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
};

export default FeaturesSection;
