import React, { useEffect, useState, useRef } from "react";
import { useLanguage } from "../../context/LanguageContext";
import "./FaqSection.css";

const FaqSection = () => {
  const { language, t } = useLanguage();
  const [faqs, setFaqs] = useState([]);
  const [filteredFaqs, setFilteredFaqs] = useState([]);
  const [expandedFaq, setExpandedFaq] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/faqs")
      .then(res => res.json())
      .then(data => setFaqs(data))
      .catch(() => setFaqs([]));
  }, []);

  const faqsToShow = faqs && faqs.length > 0 ? faqs : (t.faqs || []);
  // Категории только из faqsToShow
  const categories = [
    "all",
    ...Array.from(new Set((faqsToShow || []).map((faq) => faq.Category || faq.category).filter(Boolean)))
  ];

  useEffect(() => {
    let filtered = faqsToShow;
    if (selectedCategory !== "all") {
      filtered = filtered.filter((faq) => faq.Category === selectedCategory || faq.category === selectedCategory);
    }
    if (searchTerm) {
      filtered = filtered.filter((faq) => {
        const q = getQuestion(faq).toLowerCase();
        const a = getAnswer(faq).toLowerCase();
        return q.includes(searchTerm.toLowerCase()) || a.includes(searchTerm.toLowerCase());
      });
    }
    setFilteredFaqs(filtered);
  }, [searchTerm, selectedCategory, faqsToShow]);

  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) setIsVisible(true);
      },
      { threshold: 0.1 }
    );
    const currentSection = sectionRef.current;
    if (currentSection) observer.observe(currentSection);
    return () => {
      if (currentSection) observer.unobserve(currentSection);
    };
  }, []);

  const getQuestion = (faq) => {
    if (language === "kz") return faq.QuestionKZ || faq.question_kz;
    if (language === "en") return faq.QuestionEN || faq.question_en;
    return faq.QuestionRU || faq.question_ru;
  };
  const getAnswer = (faq) => {
    if (language === "kz") return faq.AnswerKZ || faq.answer_kz;
    if (language === "en") return faq.AnswerEN || faq.answer_en;
    return faq.AnswerRU || faq.answer_ru;
  };

  const handleFaqClick = (index) => {
    setExpandedFaq(expandedFaq === index ? null : index);
  };
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setExpandedFaq(null);
  };
  const handleCategoryChange = (categoryId) => {
    setSelectedCategory(categoryId);
    setExpandedFaq(null);
    setSearchTerm("");
  };
  const clearSearch = () => {
    setSearchTerm("");
    setExpandedFaq(null);
  };

  return (
    <section className={`faq-section${isVisible ? " animate-fade-in" : ""}`} ref={sectionRef}>
      <div className="container">
        <h2 className="faq-title">{t.faqSection?.title || "FAQ"}</h2>
        <div className="faq-controls">
          <input
            type="text"
            placeholder={t.faqSection?.searchPlaceholder || "Поиск..."}
            value={searchTerm}
            onChange={handleSearchChange}
            className="faq-search"
          />
          <button onClick={clearSearch} className="faq-clear-btn">{t.faqSection?.clear || "Очистить"}</button>
          <select value={selectedCategory} onChange={e => handleCategoryChange(e.target.value)} className="faq-category-select">
            {(categories || []).map((cat) => (
              <option key={cat} value={cat}>{cat === "all" ? t.faqSection?.all || "Все" : cat}</option>
            ))}
          </select>
        </div>
        <ul className="faq-list">
          {(filteredFaqs || []).map((faq, idx) => (
            <li className="faq-item" key={faq.id || faq._id || idx}>
              <button className="faq-question" onClick={() => handleFaqClick(idx)}>
                {getQuestion(faq)}
              </button>
              {expandedFaq === idx && (
                <div className="faq-answer">{getAnswer(faq)}</div>
              )}
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
};

export default FaqSection;
