import React, { useEffect, useState } from "react";
import { useLanguage } from "../../context/LanguageContext";
import "./NewsSection.css";

const NewsSection = () => {
  const { language, t } = useLanguage();
  const [news, setNews] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/news")
      .then(res => res.json())
      .then(data => setNews(data))
      .catch(() => setNews([]));
  }, []);

  const getTitle = (item) => {
    if (language === "kz") return item.TitleKZ || item.title_kz;
    if (language === "en") return item.TitleEN || item.title_en;
    return item.TitleRU || item.title_ru;
  };

  return (
    <section className="news-section">
      <div className="container">
        <h2 className="news-title">{t.newsSection?.title || "Новости"}</h2>
        <ul className="news-list">
          {(news || []).map((item, idx) => (
            <li className="news-item" key={item.id || item._id || idx}>
              <span className="news-title-item">{getTitle(item)}</span>
              {item.Link || item.link ? (
                <a href={item.Link || item.link} target="_blank" rel="noopener noreferrer">{item.Link || item.link}</a>
              ) : null}
              {item.Position || item.position ? (
                <span className="news-position">{item.Position || item.position}</span>
              ) : null}
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
};

export default NewsSection;
