import React from 'react';
import { useLanguage } from '../../context/LanguageContext';
import './LanguageSelector.css';

const languageLabels = {
  ru: 'Русский',
  kk: 'Қазақша',
  en: 'English',
};

export const LanguageSelector = () => {
  const { language, changeLanguage } = useLanguage();

  return (
    <div className="language-selector">
      <button
        className={`lang-btn ${language === 'ru' ? 'active' : ''}`}
        onClick={() => changeLanguage('ru')}
      >
        RU
        {language === 'ru' && <span className="lang-label">{languageLabels.ru}</span>}
      </button>
      <button
        className={`lang-btn ${language === 'kk' ? 'active' : ''}`}
        onClick={() => changeLanguage('kk')}
      >
        KK
        {language === 'kk' && <span className="lang-label">{languageLabels.kk}</span>}
      </button>
      <button
        className={`lang-btn ${language === 'en' ? 'active' : ''}`}
        onClick={() => changeLanguage('en')}
      >
        EN
        {language === 'en' && <span className="lang-label">{languageLabels.en}</span>}
      </button>
    </div>
  );
};
