import React from 'react';
import './NewsSection.css';

const newsItems = [
  {
    title: 'В мобильном приложении eGov Mobile появились ответы на вопросы о постановке в очередь на жилье',
    url: 'https://egov.kz/cms/ru/news/answers_questions',
  },
  {
    title: 'Цифровой паспорт для вашего питомца – теперь в eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/digital_pet_passports',
  },
  {
    title: 'В eGov Mobile появился сервис для проверки утечек персональных данных',
    url: 'https://egov.kz/cms/ru/news/Service_available',
  },
  {
    title: 'Признание зарубежных дипломов доступно в мобильном приложении eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/foreign_diploma2',
  },
  {
    title: 'Сообщить владельцу авто о проблеме теперь можно через eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/notifying_problem1',
  },
  {
    title: 'Замена водительского удостоверения доступна в мобильном приложении eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/driving_license',
  },
  {
    title: 'Услуга по установлению опеки стала доступна на портале eGov.kz и в мобильном приложении eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/Custody_service_egov',
  },
  {
    title: 'Справка 073/у теперь доступна в мобильном приложении eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/073_y_medical',
  },
  {
    title: 'Проверить данные о начислениях по программе «НацФонда – детям» могут пользователи портала eGov.kz и приложения eGov Mobile',
    url: 'https://egov.kz/cms/ru/news/natfund_det',
  },
];

const NewsSection = () => (
  <section className="news-section section">
    <div className="container">
      <h2 className="section-title">Новости</h2>
      <ul className="news-list">
        {newsItems.map((item, idx) => (
          <li key={idx} className="news-item">
            <a href={item.url} target="_blank" rel="noopener noreferrer" className="news-link">
              {item.title}
            </a>
          </li>
        ))}
      </ul>
    </div>
  </section>
);

export default NewsSection;
