import React from 'react';
import { FaLinkedin, FaTwitter, FaGithub, FaEnvelope } from 'react-icons/fa';
import './TeamSection.css';

const TeamSection = () => {
  const teamMembers = [
    {
      name: 'John Smith',
      role: 'Chief Executive Officer',
      description: 'Visionary leader with over 15 years of experience in technology and business management.',
      image: '/images/team/member1.jpg',
      social: {
        linkedin: 'https://linkedin.com',
        twitter: 'https://twitter.com',
        github: 'https://github.com',
        email: 'mailto:john@company.com'
      }
    },
    {
      name: 'Sarah Johnson',
      role: 'Chief Technology Officer',
      description: 'Tech innovator specializing in software architecture and emerging technologies.',
      image: '/images/team/member2.jpg',
      social: {
        linkedin: 'https://linkedin.com',
        twitter: 'https://twitter.com',
        github: 'https://github.com',
        email: 'mailto:sarah@company.com'
      }
    },
    {
      name: 'Michael Chen',
      role: 'Lead Developer',
      description: 'Full-stack developer with expertise in modern web technologies and cloud solutions.',
      image: '/images/team/member3.jpg',
      social: {
        linkedin: 'https://linkedin.com',
        twitter: 'https://twitter.com',
        github: 'https://github.com',
        email: 'mailto:michael@company.com'
      }
    },
    {
      name: 'Emily Davis',
      role: 'UX/UI Designer',
      description: 'Creative designer focused on creating intuitive and engaging user experiences.',
      image: '/images/team/member4.jpg',
      social: {
        linkedin: 'https://linkedin.com',
        twitter: 'https://twitter.com',
        github: 'https://github.com',
        email: 'mailto:emily@company.com'
      }
    }
  ];

  return (
    <section className="team-section" id="team">
      <div className="team-container">
        <div className="team-header">
          <h2>Our Team</h2>
          <p>
            Meet our talented team of professionals who are passionate about
            delivering exceptional solutions and driving innovation forward.
          </p>
        </div>

        <div className="team-grid">
          {teamMembers.map((member, index) => (
            <div key={index} className="team-member">
              <div className="member-image">
                <img src={member.image} alt={member.name} loading="lazy" />
              </div>
              <div className="member-info">
                <h3 className="member-name">{member.name}</h3>
                <div className="member-role">{member.role}</div>
                <p className="member-description">{member.description}</p>
                <div className="member-social">
                  <a href={member.social.linkedin} className="social-link" target="_blank" rel="noopener noreferrer">
                    <FaLinkedin />
                  </a>
                  <a href={member.social.twitter} className="social-link" target="_blank" rel="noopener noreferrer">
                    <FaTwitter />
                  </a>
                  <a href={member.social.github} className="social-link" target="_blank" rel="noopener noreferrer">
                    <FaGithub />
                  </a>
                  <a href={member.social.email} className="social-link">
                    <FaEnvelope />
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TeamSection;
