import React from 'react';
import { FaUserPlus, FaFileAlt, FaCheckCircle, FaUpload, FaEnvelope, FaDownload } from 'react-icons/fa';
import './InstructionsSection.css';

const InstructionsSection = () => {
  const instructions = [
    {
      number: '01',
      icon: <FaUserPlus />,
      title: 'Create Account',
      description: 'Start by creating your account on our platform. Provide your basic information to get started.',
      link: '#register'
    },
    {
      number: '02',
      icon: <FaFileAlt />,
      title: 'Fill Application',
      description: 'Complete the application form with all required details and supporting documents.',
      link: '#apply'
    },
    {
      number: '03',
      icon: <FaCheckCircle />,
      title: 'Review Process',
      description: 'Our team will review your application and verify all submitted information.',
      link: '#review'
    },
    {
      number: '04',
      icon: <FaUpload />,
      title: 'Upload Documents',
      description: 'Upload all necessary documents and certificates to support your application.',
      link: '#upload'
    },
    {
      number: '05',
      icon: <FaEnvelope />,
      title: 'Receive Confirmation',
      description: 'Get notified about the status of your application via email.',
      link: '#notify'
    },
    {
      number: '06',
      icon: <FaDownload />,
      title: 'Download Certificate',
      description: 'Once approved, download your certificate or document from your dashboard.',
      link: '#download'
    }
  ];

  return (
    <section className="instructions-section" id="instructions">
      <div className="instructions-container">
        <div className="instructions-header">
          <h2>How It Works</h2>
          <p>
            Follow these simple steps to complete your application process.
            Our streamlined system makes it easy to get started and track your progress.
          </p>
        </div>

        <div className="instructions-grid">
          {instructions.map((instruction, index) => (
            <div key={index} className="instruction-card">
              <div className="instruction-number">{instruction.number}</div>
              <div className="instruction-icon">
                {instruction.icon}
              </div>
              <h3 className="instruction-title">{instruction.title}</h3>
              <p className="instruction-description">{instruction.description}</p>
              <a href={instruction.link} className="instruction-link">
                Learn More
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
              </a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default InstructionsSection;
